import './App.css';

import React, { Component } from 'react';

import ClientColumn from './ClientColumn';
import FormColumn from './FormColumn';
import ResponseColumn from './ResponseColumn';
import logo from './logo.svg';

class App extends Component {
  state = {
    clients: [],
    selectedClient: null,
    response: null
  };
  componentDidMount() {
    this.getClients();
  }
  getClients = async () => {
    const response = await fetch('/api/clients');
    if (response.status !== 200) throw Error(response.message);
    const clients = await response.json();
    this.setState({ clients });
  };
  selectClient = selectedClient => () => {
    this.setState({ selectedClient });
  }
  render() {
    const clients = this.state.clients;
    const selectedClient = this.state.selectedClient;
    const response = this.state.response;
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Summer 2019 Internship Technical Challenge.
          </p>
        </header>
        <div className="App-body">
          {clients.length > 0 && (
            <ClientColumn clients={clients} selectClient={this.selectClient} />
          )}
          {selectedClient && (
            <FormColumn client={selectedClient} />
          )}
          {response && (
            <ResponseColumn response={response} />
          )}
        </div>
      </div>
    );
  }
}

export default App;
