import './FormColumn.css';

import React, { Component } from 'react';

import PropTypes from 'prop-types';

class FormColumn extends Component {
  state = {
    currency: 'usd',
  };
  onChange = event => {
    const state = this.state;
    state[event.target.name] = event.target.value;
    this.setState(state);
  }
  checkIfValid = () => {
    const state = this.state;
    return state.amount && state.amount.length > 0
    && state.currency && state.currency.length > 0
    && state.source && state.source.length > 0
  }
  render() {
    const client = this.props.client;
    return (
      <form className="Form-col" onSubmit={this.props.handleSubmit}>
        <h2>New Payment</h2>
        <p>{client.firstName} {client.lastName}</p>
        <input
          name="amount"
          placeholder="Amount"
          type="text"
          value={this.state.amount}
          onChange={this.onChange}
        />
        <br />
        <select name="currency" value={this.state.currency} onChange={this.onChange}>
          <option value="euro">Euro</option>
          <option value="rupee">Rupee</option>
          <option value="usd">USD</option>
        </select>
        <br />
        <input
          name="source"
          placeholder="Source"
          type="text"
          value={this.state.source}
          onChange={this.onChange}
        />
        <br />
        <br />
        <button type="submit" disabled={!this.checkIfValid()}>Submit</button>
      </form>
    );
  }
}

FormColumn.propTypes = {
  client: PropTypes.object.isRequired,
  handleSubmit: PropTypes.func.isRequired
};

export default FormColumn;
