import './FormColumn.css';

import React, { Component } from 'react';

import PropTypes from 'prop-types';
import moment from 'moment';

class FormColumn extends Component {
  checkIfValid = () => {
    const client = this.props.client;
    const state = this.state;
    return client.paymentStatus === 'Overdue' 
    && state.amount && state.amount.length > 0
    && state.currency && state.currency.length > 0
    && state.source && state.source.length > 0
  }
  render() {
    const client = this.props.client;
    const payment = this.props.payment;
    const name = `${client.firstName} ${client.lastName}`
    return (
      <form className="Form-col" onSubmit={this.props.handleSubmit}>
        <h2>New payment request</h2>
        <p>{name}</p>
        <input
          name="amount"
          placeholder="Amount"
          type="text"
          value={payment.amount}
          onChange={this.onChange}
        />
        <br />
        <select name="currency" value={payment.currency} onChange={this.onChange}>
          <option value="euro">Euro</option>
          <option value="rupee">Rupee</option>
          <option value="usd">USD</option>
        </select>
        <br />
        <input
          name="source"
          placeholder="Source"
          type="text"
          value={client.source}
          disabled
        />
        <br />
        <input
          name="cvc"
          placeholder="CVC"
          type="text"
          value={client.cvc}
          disabled
        />
        <br />
        <input
          name="expiry"
          placeholder="Source"
          type="text"
          value={moment(client.expiry).format('MMM dd YYYY')}
          disabled
        />
        <br />
        <br />
        <button type="submit" disabled={!this.checkIfValid()}>Submit</button>
        {payment.status === 'Paid' && <div>This has already been paid</div>}
        {payment.status === 'Requested' && <div>This has already been requested</div>}
        {moment(client.expiry).isAfter(moment()) && <div>Card is expired</div>}
      </form>
    );
  }
}

FormColumn.propTypes = {
  client: PropTypes.object.isRequired,
  payment: PropTypes.object.isRequired,
  updatePayment: PropTypes.object.isRequired,
  handleSubmit: PropTypes.func.isRequired
};

export default FormColumn;
