import './App.css';

import React, { Component } from 'react';

import ClientColumn from './ClientColumn';
import FormColumn from './FormColumn';
import ResponseColumn from './ResponseColumn';
import logo from './logo.svg';

class App extends Component {
  state = {
    clients: []
  };
  componentDidMount() {
    this.getClients();
  }
  getClients = async (payment_status) => {
    const response = await fetch(`/api/clients${payment_status ? `?payment_status=${payment_status}` : ''}`);
    if (response.status !== 200) throw Error(response.message);
    const clients = await response.json();
    this.setState({ clients });
  };
  selectClient = selectedClient => () => {
    this.setState({ selectedClient });
  }
  updateClient = updatedClient => () => {
    const clients = this.state.clients.filter(c => c.id === updatedClient.id);
    clients.push(updatedClient);
    this.setState({ updatedClient });
  }
  selectFilter = payment_status => {
    // TO DO
    this.getClients(payment_status);
  }
  render() {
    const clients = this.state.clients;
    const selectedClient = this.state.selectedClient;
    const response = this.state.response;
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Summer 2019 Internship Technical Challenge.
          </p>
        </header>
        <div className="App-body">
          {clients.length > 0 && (
            <ClientColumn
              clients={clients}
              selectedClient={selectedClient}
              selectClient={this.selectClient}
              selectFilter={this.selectFilter}
            />
          )}
          {selectedClient && (
            <FormColumn
              client={selectedClient}
              updateClient={this.updateClient}
            />
          )}
          {response && (
            <ResponseColumn response={response} />
          )}
        </div>
      </div>
    );
  }
}

export default App;
